@import url("https://fonts.googleapis.com/css2?family=Germania+One:wght@400;500;700&&family=Rye:wght@400;500;700&&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Kameron&family=Libre+Baskerville&display=swap");

/* Author: Don Ricardo Jr, UI/UX Front End Developer @ DonarDevelopment.com */

/* ==========================================================================
  Base styles: opinionated defaults
========================================================================== */

html {
  color: aliceblue;
  font-size: 1em;
  line-height: 1.4;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #270908;
  scroll-behavior: smooth; /* Thanks Chris Coyier @ CSS-tricks.com */
}

/*
   * Remove text-shadow in selection highlight:
   * https://twitter.com/miketaylr/status/12228805301
   *
   * Vendor-prefixed and regular ::selection selectors cannot be combined:
   * https://stackoverflow.com/a/16982510/7133471
   *
   * Customize the background color to match your design.
   */

::-moz-selection {
  background: #fcfcfc;
  text-shadow: none;
}

::selection {
  background: #fcfcfc;
  text-shadow: none;
}

/*
   * A better looking default horizontal rule
   */

hr {
  display: block;
  height: 1px;
  border: 0;
  border-top: 1px solid #ccc;
  margin: 1em 0;
  padding: 0;
}

/*
   * Remove the gap between audio, canvas, iframes,
   * images, videos and the bottom of their containers:
   * https://github.com/h5bp/html5-boilerplate/issues/440
   */

audio,
canvas,
iframe,
img,
svg,
video {
  vertical-align: middle;
}

/*
   * Remove default fieldset styles.
   */

fieldset {
  border: 0;
  margin: 0;
  padding: 0;
}

/*
   * Allow only vertical resizing of textareas.
   */

textarea {
  resize: vertical;
}

/* ==========================================================================
    Author's custom styles
========================================================================== */

*,
*::before,
*::after {
  box-sizing: border-box;
}

body {
  padding: 0;
  margin: 0;
  width: 100vw;
  height: 100vh;
  background-color: #270908;
  color: aliceblue;
  font-family: "Kameron", "Libre Baskerville", serif;
}

.header-section {
  padding: 0.5em 0 2em 0;
  margin: 0.5em 0 4em 0;
  width: 100vw;
  height: auto;
  background-color: #222;
  color: aliceblue;
  font-family: "Kameron", "Libre Baskerville", serif;
  background: url("../img/hero.jpg") center;
  background-attachment: fixed;
  background-repeat: no-repeat;
  background-size: cover;
}

article,
aside,
figcaption,
figure,
footer,
header,
hgroup,
main,
nav,
section {
  display: block;
}

h1,
h2,
h3,
h4,
nav {
  font-family: "Rye", "Germania One", serif;
  color: aliceblue;
}

p {
  font-family: "Kameron", "Libre Baskerville", serif;
  font-size: 1.25em;
  color: aliceblue;
}

.gold {
  color: aliceblue;
}

.accent-color,
.card-text {
  color: #33251f;
}

.accent-text,
h3.card-title,
#body > section > .card-body > h3.card-title,
div.more a {
  color: #33251f;
  font-size: 3em;
}

.container a,
.nav-link {
  color: aliceblue;
  text-decoration: none;
}

.nav-bg-color {
  background: #222;
}

.btn-warning {
  background-color: aliceblue !important;
  color: #222 !important;
  font-weight: bold;
  font-size: 1em;
  padding: 0.3em;
  border-radius: 9px;
}

.btn-warning:hover {
  background-color: goldenrod !important;
  text-decoration: none;
  color: #222 !important;
}

#mainNav {
  margin-right: 1em;
}

.navbar-brand {
  font-weight: normal;
  font-size: 1.5em;
}

.nav-item {
  font-size: 1.5em;
}

.fa-bars {
  color: aliceblue;
}

.nav-link:hover {
  color: #ffc107;
}

#hero {
  display: table;
  width: 100%;
  height: 100vh;
}

.hero-logo {
  margin: -3.5em auto 0 auto;
  max-width: 100%;
  text-align: center;
}

.hero-logo h1 {
  font-size: 6em;
  margin: 2em 0 0.5em 0;
  text-align: center;
}

.hero-logo h2 {
  margin-top: -0.1em;
  padding-bottom: 0.9em;
  text-align: center;
}

#hero .hero-container {
  display: table-cell;
  background: rgba(0, 0, 0, 0.5);
  margin: 0;
  padding: 0;
  text-align: center;
  vertical-align: middle;
}

div.cta {
  padding-bottom: 3em;
}

a.menu-link {
  color: aliceblue;
  font-size: 1.5em;
  margin: 3em;
}

#menu {
  padding: none !important;
}

#contact {
  width: 100%;
  height: auto;
  background-color: #222;
  background-image: url("../img/rider-cropped.jpg");
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  background-attachment: fixed;
  clip-path: polygon(0 10%, 100% 2%, 100% 90%, 0 100%);
}

#contact-margin {
  margin: 2em 0 2em 20em;
  padding: 2em 0 2em 0;
}

#bottom-margin {
  margin: 2em 5em;
}

.contact-form {
  background-color: #270908;
  width: 60% !important;
  height: auto;
  padding: 2em;
  border-radius: 6px;
  margin: 0 0 0 10em !important;
  font-weight: normal;
  letter-spacing: 0.1rem;
}

.submit-btn {
  border-radius: 5px;
  font-family: "Rye";
}

.hidden {
  display: none;
}

.required::after {
  content: "*";
  color: red;
}

.input-group {
  margin-bottom: 2rem;
  display: flex;
  flex-direction: column;
}

.input-group:last-child {
  margin-bottom: 0;
}

.input-group label {
  font-weight: bold;
  margin-bottom: 0.5rem;
  display: flex !important;
}

.input-group input {
  outline: none;
  background: none;
  border: 1px solid #ccc;
  font-size: 1em;
  padding: 0.5em;
  color: inherit;
  border-radius: 4px;
}

.input-group input:focus {
  border-color: #fff;
}

#image {
  width: 100%;
  height: 100vh;
  margin: 8vh auto 0 auto;
  background-color: #222;
  background-image: none;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  background-attachment: fixed;
  clip-path: polygon(0 10%, 100% 2%, 100% 90%, 0 100%);
}

.image-section {
  margin-top: 10vh;
  padding: 2em 0 0 0;
}

.card {
  margin-bottom: 10vh !important;
  min-width: 100% !important;
}

.donar_dev {
  color: aliceblue;
  text-decoration: none;
}

a:hover,
a.donar_dev:hover {
  color: #ffc107 !important;
  text-decoration: none;
}

a.footer-p-gold:hover {
  color:antiquewhite !important;
}

h2#main-title {
  font-size: 3em;
  font-family: "Rye", "Germania One", cursive;
  padding: 3em 0 1em;
}

.section-title p {
  font-size: 1.5em;
  color: aliceblue;
  padding: 0 0 3em 0;
  text-align: center;
}

.section-title > .services-p {
  font-size: 2em;
  color: aliceblue;
  padding: 0.5em 2em;
  text-align: center;
  letter-spacing: 0.025rem;
}

.logo-p {
  font-size: 3em;
  font-family: "Rye";
  margin: 3em 0 1rem 0;
  letter-spacing: -0.5rem;
}

.section-title p.logo-p {
  font-size: 6em;
  font-family: "Rye";
  margin: 0;
  padding: 0 0 2rem 0;
  letter-spacing: -0.5rem;
}

#contact-padding p {
  font-size: 1em;
  font-weight: bold;
  color: aliceblue;
  padding: 0 0 2em 0;
  text-align: center;
}

.image-section h2 {
  font-size: 2.5em;
  font-weight: bold;
  color: aliceblue;
  opacity: 0.9;
  padding: 2em 0 2em 4em;
  text-align: center;
}

.section-title div.row {
  padding-left: 3vw;
}

#contact-section {
  font-size: 2em;
  margin: .8em 0 0 0 !important;
  font-family: "Rye", "Germania One", "Lato", sans-serif;
  color: aliceblue;
}

#contact > div > div:nth-child(2) > div:nth-child(1) > div > h5 {
  margin-top: -4vh;
}

div.more a:hover {
  color: #ccc;
}

#mainMessage {
  font-family: "Kameron", "Libre Baskerville", serif;
}

div.contact-title h2 {
  font-size: 4em;
  font-family: "Rye", "Germania One", cursive;
}

.contact-title p {
  font-size: 0.9em;
}

#contact-heading.contact-h1 {
  font-size: 4em !important;
  padding: 1em 0 0 0;
}

a[href^="tel:"] {
  color: #ffc107;
  text-decoration: none;
}

.mb-5,
.my-5 {
  margin-bottom: 2rem !important;
}

#hero-logo-img {
  display: none;
}

#footer-h2 {
  margin-top: 0;
  font-size: 1.5em;
}

.section-title > .footer-p {
  font-size: 0.8em;
  font-weight: normal;
  text-transform: uppercase;
  padding: 0.5em 1em;
}

.footer-p-gold {
  color: #ffc107 !important;
}

#contact-margin > div > form > div:nth-child(6) > input[type="text-area"] {
  padding: 0 0 2.5em 0;
}

#contact-margin > div > p > a {
  padding-left: 0.025rem;
}

/***** MEDIA QUERIES *****/

@media screen and (max-device-width: 1024px) {
  #hero {
    width: 100%;
    height: 100vh;
  }

  .hero-logo h1 {
    font-size: 3em;
    margin: 2.5em 0 1em 0;
  }

  .hero-logo h2 {
    text-align: center;
    margin-bottom: 0.5em;
    font-size: 1.5rem;
  }

  h2#main-title {
    font-size: 2.5em;
    padding-top: 1em;
  }

  #contact-margin {
    margin: 0 1em;
  }

  .contact-form {
    background-color: #270908;
    width: 50% !important;
    height: auto;
    padding: 2em;
    border-radius: 6px;
    margin: auto !important;
    font-weight: 400;
    letter-spacing: 0.1rem;
  }

  #bottom-margin {
    margin: 1em !important;
  }

  #footer-p {
    font-size: 0.7em !important;
    font-weight: normal;
  }

  .navbar-brand img {
    margin-right: 0.25rem;
  }

  .navbar-brand {
    font-size: 0.75em;
  }

  #main-navbar {
    margin-top: 0.5em;
  }

  #contact div.container {
    margin-top: 8vh;
  }

  .hero-logo {
    max-width: 100%;
    text-align: center;
  }

  .hero-logo h3 {
    margin-bottom: 1.5em;
  }

  #navbar-toggler {
    margin-bottom: 5em;
  }

  .header-section {
    padding: 0.5em 0 0 0;
    width: 100vw;
    height: 100vh;
  }
}

/* ----------- iPad 1, 2, Mini and Air ----------- */

/* Portrait */
@media only screen and (min-device-width: 768px) and (max-device-width: 1024px) and (orientation: portrait) and (-webkit-min-device-pixel-ratio: 1) {
  .hero-logo h1 {
    font-size: 4em;
    margin: 2.5em 0 0.3em 0;
  }

  h2#main-title {
    font-size: 2.5em;
    padding: 1em 0 0 0;
    margin: 2em 0 0 0 !important;
  }

  .header-section {
    padding: 0.5em 0 0 0;
    margin: 0.5em 0 3em 0;
    width: 100vw;
    height: 70vh;
  }

  .accent-text,
  h3.card-title,
  #body > section > .card-body > h3.card-title,
  div.more a {
    color: #33251f;
    font-size: 1.4em;
  }

  .card-body {
    padding: 1em !important;
    margin: 0 !important;
  }

  .container {
    width: 100%;
    padding: 0;
    margin-right: auto;
    margin-left: auto;
  }
}

/* Landscape */
@media only screen and (min-device-width: 768px) and (max-device-width: 1024px) and (orientation: landscape) and (-webkit-min-device-pixel-ratio: 1) {
  .hero-logo h1 {
    font-size: 4em;
    margin: 2.5em 0 0.3em 0;
  }
  .header-section {
    padding: 0.5em 0 0 0;
    margin: 0.5em 0 4em 0;
    width: 100vw;
    height: 80vh;
  }
  .contact-form {
    width: 60% !important;
  }

  .section-title p.logo-p {
    font-size: 4em;
    font-family: "Rye";
    margin: 0;
    padding: 0 0 2rem 0;
    letter-spacing: -0.5rem;
  }

  .card-title {
    margin: none;
  }
}

@media screen and (max-device-width: 800px) {
  h2#main-title {
    font-size: 1.75em;
    padding: 0 0.5em 1em;
  }

  .contact-form {
    background-color: #270908;
    width: 95% !important;
    height: auto;
    padding: 2em;
    border-radius: 6px;
    margin: auto !important;
    font-weight: 400;
    letter-spacing: 0.1rem;
  }

  .section-title p {
    font-size: 1em;
    color: aliceblue;
    padding: 0 0 3em 0;
    text-align: center;
  }

  .section-title p.logo-p {
    font-size: 4em;
    font-family: "Rye";
    margin: 0;
    padding: 0 0 2rem 0;
    letter-spacing: -0.5rem;
  }

  .section-title > .services-p {
    font-size: 1.5em;
    color: aliceblue;
    padding: 0.5em 2em;
    text-align: center;
    letter-spacing: 0.025rem;
  }

  .accent-text,
  h3.card-title,
  #body > section > .card-body > h3.card-title,
  div.more a {
    color: #33251f;
    font-size: 1.8em;
  }

  .card-body {
    padding: 1em !important;
    margin: 0 !important;
  }

  .container {
    width: 100%;
    /* padding-right: 15px; */
    /* padding-left: 15px; */
    margin-right: auto;
    margin-left: auto;
  }
}
